INSERT INTO `product` VALUES ('240', '北戴河海边田园主题家庭公寓冰箱上网小吃步行街', 'c446f4fb-1ef5-4bdc-b749-12deacb66e64.jpg', '263.00', '9a12cfcd-3296-4151-a845-a884090ffde8.jpg', 'bdhyslw', '整套', '2室1厅', '2张床', '50㎡', ' 北戴河明海御墅龙湾公寓处于北戴河繁华地段，紧临北戴河小吃一条街和夜市，交通、购物和去海边都很方便。公寓环境幽雅，绿草茵茵，鸟语花香，海风习习，清雅幽静，实乃携情侣、家人来此度假首选场所。出门看风景、海边漫步，观海听涛，追波逐浪。欧版装修设计格局，典雅别致，浪漫温馨，以情侣房，家庭房为主，充分体现人文和现代风格以及环保理念。', '房主人很好，什么问题都会耐心解答，而且打电话麻烦房主无数次，也都不会不耐烦。屋子很干净，刚一进去就觉得，哇，心情好好。设备也很齐全，各种东西都贴有温馨提示。很是暖人心。下次再来，继续光顾，还是“麻烦”房主，很棒的一次居住！', '10', '生活必需品，空调，暖气，洗衣机，衣架，熨斗，洗衣机，衣架，熨斗', '24', '中国，河北省，秦皇岛', null, 'comment.png', 'uncollection.png,hascollectioned.png', null, null);
